import React, { useState } from "react";
import "./Filter.css";
import SearchBar from "./SearchBar.jsx";

const Filter = () => {
  const [selectedAnimals, setSelectedAnimals] = useState([]);

  const handleCheckboxChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedAnimals((prevSelectedAnimals) => [...prevSelectedAnimals, value]);
    } else {
      setSelectedAnimals((prevSelectedAnimals) =>
        prevSelectedAnimals.filter((animal) => animal !== value)
      );
    }
  };

  return (
    <div className="container-filter">
      <hr className="line-filter"></hr>
      <div>
        <h3>ประเภทสัตว์เลี้ยง</h3>
        <ul className="items-filter">
          <li>
            <div className="item-type1">
              <input
                id="cat-checkbox"
                type="checkbox"
                value="cat"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="cat-checkbox">แมว</label>
            </div>
          </li>
          <li>
            <div className="item-type2">
              <input
                id="dog-checkbox"
                type="checkbox"
                value="dog"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="dog-checkbox">สุนัข</label>
            </div>
          </li>
          <li>
            <div className="flex items-center mr-4">
              <input
                id="bird-checkbox"
                type="checkbox"
                value="bird"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="bird-checkbox">นก</label>
            </div>
          </li>
          <li>
            <div className="flex items-center mr-4">
              <input
                id="rabbit-checkbox"
                type="checkbox"
                value="rabbit"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="rabbit-checkbox">กระต่าย</label>
            </div>
          </li>
          <li>
            <div className="flex items-center mr-4">
              <input
                id="rodent-checkbox"
                type="checkbox"
                value="rodent"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="rodent-checkbox">สัตว์ฟันแทะ</label>
            </div>
          </li>
          <li>
            <div className="flex items-center mr-4">
              <input
                id="reptile-checkbox"
                type="checkbox"
                value="reptile"
                onChange={handleCheckboxChange}
              />
              <label htmlFor="reptile-checkbox">สัตว์เลื้อยคลาน</label>
            </div>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Filter;
