import { useState } from 'react';
import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundAdmin from '../images/bgAdmin.png';
import imageLogo from '../images/asd.png';
import VerifyStore from './AdminComponents/VerifyStore';
import MangeAccount from './AdminComponents/MangeAccount';
import ReportingService from './AdminComponents/ReportingService';

const sideBarAdmin = () => {

    const [selectedButton, setSelectedButton] = useState(null);
    const navigate = useNavigate();

    const handleButtonClick = (buttonId) => {
        setSelectedButton(buttonId);
        switch (buttonId) {
            case 'account':
                navigate('account');
                break;
            case 'store':
                navigate('store');
                break;
            case 'report':
                navigate('report');
                break;
            default:
                // Handle default case if needed
                break;
        }
    };

    const renderComponent = () => {
        switch (selectedButton) {
            case 'account':
                return <MangeAccount/>;
            case 'store':
                return <VerifyStore/>;
            case 'report':
                return <ReportingService/>;
                // break;
            default:
                return 'ยินดีต้อนเข้ารับสู่ระบบของผู้ดูแลระบบคาบิซู';
        }
    };

    return (
        <div className='welcomeAdminPage'>
            <div className='sideMenu'>
                <ul className='menuOnSidebar'>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'account' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('account')}
                        >
                            จัดการบัญชีผู้ใช้งาน
                        </button>
                    </li>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'store' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('store')}
                        >
                            ยืนยันการมีหน้าร้าน
                        </button>
                    </li>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'report' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('report')}
                        >
                            การรายงาน
                        </button>
                    </li>
                </ul>
                <div className='frameImageOnSidebar'>
                    <img className='imageOnSidebar' src={imageLogo} alt="Logo" />
                </div>
            </div>
            <div className='picAdminPage' style={{ backgroundImage: `url(${backgroundAdmin})` }}>
                <h1 className='welcomeAdmin'>{renderComponent()}</h1>
            </div>
        </div>
    );
}

export default sideBarAdmin;
