import React, { useState } from 'react';
import './AdministratorHomepage.css';
import backgroundAdmin from '../../images/bgAdmin.png';
import imageLogo from '../../images/asd.png';
import VerifyStore from '../../components/AdminComponents/VerifyStore';

const AdministratorHomepage = () => {
    const [selectedButton, setSelectedButton] = useState(null);
    const [displayResult, setDisplayResult] = useState('ยินดีต้อนเข้ารับสู่ระบบของผู้ดูแลระบบคาบิซู');

    const handleButtonClick = (buttonId, result) => {
        setSelectedButton(buttonId);
        setDisplayResult(result);
    };

    const renderComponent = () => {
        switch (selectedButton) {
            case 'account':
                return <VerifyStore/>;
        }
    }

    return (
        <div className='welcomeAdminPage'>
            <div className='sideMenu'>
                <ul className='menuOnSidebar'>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'account' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('account', 'Display Account')}
                        >
                            จัดการบัญชีผู้ใช้งาน
                        </button>
                    </li>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'store' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('store', 'Display Store')}
                        >
                            ยืนยันการมีหน้าร้าน
                        </button>
                    </li>
                    <li className='itemListAdmin'>
                        <button
                            className={`btItem ${selectedButton === 'report' ? 'clicked' : ''}`}
                            onClick={() => handleButtonClick('report', 'Display Report')}
                        >
                            การรายงาน
                        </button>
                    </li>
                </ul>
                <div className='frameImageOnSidebar'>
                    <img className='imageOnSidebar' src={imageLogo} alt="Logo" />
                </div>
            </div>
            <div className='picAdminPage' style={{ backgroundImage: `url(${backgroundAdmin})` }}>
                <h1 className='welcomeAdmin'>{displayResult}</h1>
            </div>
        </div>
    );
}

export default AdministratorHomepage;
